{"content": "using OpenLiveWriterPostCreator;

class Program
{
    static void Main(string[] args)
    {
        Console.WriteLine("OpenLive Writer Post Creator");
        Console.WriteLine("=============================");

        while (true)
        {
            Console.WriteLine("\nChoose an option:");
            Console.WriteLine("1. Create a new post");
            Console.WriteLine("2. Load and display an existing post");
            Console.WriteLine("3. Create a sample Friday Links post");
            Console.WriteLine("4. Exit");
            Console.Write("Enter your choice (1-4): ");

            var choice = Console.ReadLine();

            switch (choice)
            {
                case "1":
                    CreateNewPost();
                    break;
                case "2":
                    LoadExistingPost();
                    break;
                case "3":
                    CreateSampleFridayLinksPost();
                    break;
                case "4":
                    Console.WriteLine("Goodbye!");
                    return;
                default:
                    Console.WriteLine("Invalid choice. Please try again.");
                    break;
            }
        }
    }

    static void CreateNewPost()
    {
        Console.WriteLine("\n--- Create New Post ---");

        var post = new BlogPost();

        Console.Write("Enter post title: ");
        post.Title = Console.ReadLine() ?? "";

        Console.Write("Enter blog name: ");
        var blogName = Console.ReadLine();
        if (!string.IsNullOrWhiteSpace(blogName))
            post.BlogName = blogName;

        Console.Write("Enter author name (or press Enter for current user): ");
        var author = Console.ReadLine();
        if (!string.IsNullOrWhiteSpace(author))
            post.Author = author;

        Console.WriteLine("Enter post content (type 'END' on a new line to finish):");
        var contentLines = new List<string>();
        string line;
        while ((line = Console.ReadLine()) != "END")
        {
            contentLines.Add(line ?? "");
        }
        post.Content = string.Join("\n", contentLines);

        Console.Write("Enter excerpt (optional): ");
        post.Excerpt = Console.ReadLine() ?? "";

        Console.Write("Enter categories (comma-separated): ");
        var categories = Console.ReadLine()?.Split(',', StringSplitOptions.RemoveEmptyEntries);
        if (categories != null)
            post.Categories.AddRange(categories.Select(c => c.Trim()));

        Console.Write("Enter tags (comma-separated): ");
        var tags = Console.ReadLine()?.Split(',', StringSplitOptions.RemoveEmptyEntries);
        if (tags != null)
            post.Tags.AddRange(tags.Select(t => t.Trim()));

        Console.Write("Is this a draft? (y/n): ");
        var isDraft = Console.ReadLine()?.ToLower().StartsWith("y") ?? true;
        post.IsDraft = isDraft;

        var fileName = $"{SanitizeFileName(post.Title)}_{DateTime.Now:yyyyMMdd_HHmmss}.xml";
        var filePath = Path.Combine(Directory.GetCurrentDirectory(), fileName);

        OpenLiveWriterPostGenerator.SavePost(post, filePath);

        Console.WriteLine($"\nPost saved successfully to: {filePath}");
        Console.WriteLine($"Post ID: {post.PostId}");
    }

    static void LoadExistingPost()
    {
        Console.WriteLine("\n--- Load Existing Post ---");
        Console.Write("Enter the path to the XML file: ");
        var filePath = Console.ReadLine();

        if (string.IsNullOrWhiteSpace(filePath) || !File.Exists(filePath))
        {
            Console.WriteLine("File not found or invalid path.");
            return;
        }

        try
        {
            var post = OpenLiveWriterPostGenerator.LoadPost(filePath);
            DisplayPost(post);
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error loading post: {ex.Message}");
        }
    }

    static void CreateSampleFridayLinksPost()
    {
        Console.WriteLine("\n--- Creating Sample Friday Links Post ---");

        var post = new BlogPost
        {
            Title = $"Friday Links - {DateTime.Now:MMMM dd, yyyy}",
            BlogName = "Tech Weekly",
            Author = Environment.UserName,
            Content = @"<h2>Development Tools</h2>
<ul>
<li><a href=""https://github.com/microsoft/vscode"">Visual Studio Code</a> - Lightweight but powerful source code editor</li>
<li><a href=""https://dotnet.microsoft.com/"">ASP.NET Core</a> - Cross-platform web framework for .NET</li>
</ul>

<h2>Interesting Articles</h2>
<ul>
<li><a href=""https://example.com/article1"">The Future of Web Development</a> - A deep dive into emerging trends</li>
<li><a href=""https://example.com/article2"">Best Practices for API Design</a> - Essential guidelines for REST APIs</li>
</ul>

<h2>Open Source Projects</h2>
<ul>
<li><a href=""https://github.com/awesome/project"">Awesome Project</a> - This week's featured open source project</li>
</ul>

<p>Have a great weekend! </p>",
            Excerpt = "Weekly collection of interesting development links and resources.",
            Categories = { "Tech", "Weekly Roundup" },
            Tags = { "links", "development", "friday", "roundup", "programming" },
            IsDraft = false
        };

        var fileName = $"friday_links_{DateTime.Now:yyyyMMdd}.xml";
        var filePath = Path.Combine(Directory.GetCurrentDirectory(), fileName);

        OpenLiveWriterPostGenerator.SavePost(post, filePath);

        Console.WriteLine($"\nSample Friday Links post created successfully!");
        Console.WriteLine($"File: {filePath}");
        Console.WriteLine($"Post ID: {post.PostId}");

        DisplayPost(post);
    }

    static void DisplayPost(BlogPost post)
    {
        Console.WriteLine("\n--- Post Details ---");
        Console.WriteLine($"Title: {post.Title}");
        Console.WriteLine($"Author: {post.Author}");
        Console.WriteLine($"Blog: {post.BlogName}");
        Console.WriteLine($"Created: {post.CreatedDate}");
        Console.WriteLine($"Status: {(post.IsDraft ? "Draft" : "Published")}");
        Console.WriteLine($"Post ID: {post.PostId}");

        if (post.Categories.Any())
            Console.WriteLine($"Categories: {string.Join(", ", post.Categories)}");

        if (post.Tags.Any())
            Console.WriteLine($"Tags: {string.Join(", ", post.Tags)}");

        if (!string.IsNullOrWhiteSpace(post.Excerpt))
            Console.WriteLine($"Excerpt: {post.Excerpt}");

        Console.WriteLine($"\nContent Preview:");
        Console.WriteLine(new string('-', 40));
        var preview = post.Content.Length > 200 ? post.Content.Substring(0, 200) + "..." : post.Content;
        Console.WriteLine(preview);
        Console.WriteLine(new string('-', 40));
    }

    static string SanitizeFileName(string fileName)
    {
        var invalidChars = Path.GetInvalidFileNameChars();
        var sanitized = new string(fileName.Where(c => !invalidChars.Contains(c)).ToArray());
        return string.IsNullOrWhiteSpace(sanitized) ? "untitled" : sanitized;
    }
