    public static void SavePost(BlogPost post, string filePath)
    {
        // NOTE: The actual .wpost format used by OpenLive Writer uses Microsoft's Structured Storage
        // (COM-based compound document format), which requires additional references and is complex to implement.
        // 
        // For this demo, we'll create a simplified XML format that mimics the structure
        // but saves it with the .wpost extension. A full implementation would require:
        // 1. Microsoft.Office.Interop.Word or similar COM references
        // 2. Implementation of the full compound document structure with multiple streams
        // 3. Proper handling of supporting files (images, etc.)
        //
        // The real OpenLive Writer format stores data in multiple streams within a compound document:
        // - "DestinationBlogId" stream
        // - "Id" stream  
        // - "Title" stream
        // - "Contents" stream (UTF-8 encoded)
        // - "SupportingFiles" storage with sub-streams for images/files
        // - Various other metadata streams
        
        CreateStructuredStorageCompatibleFile(post, filePath);
    }

    private static void CreateStructuredStorageCompatibleFile(BlogPost post, string filePath)
    {
        // This creates an XML-based approximation of the .wpost structure
        // A real implementation would use COM Structured Storage APIs
        
        var document = new XDocument(
            new XDeclaration("1.0", "utf-8", null),
            new XComment("OpenLive Writer Post File - Simplified Format"),
            new XComment("Real .wpost files use Microsoft Structured Storage (Compound Document) format"),
            new XElement("BlogPost",
                new XAttribute("format-version", "2.0"),
                new XAttribute("blog-id", post.BlogId),
                new XAttribute("post-id", post.PostId),
                new XAttribute("created-date", post.CreatedDate.ToString("yyyy-MM-ddTHH:mm:ss")),
                new XAttribute("is-draft", post.IsDraft.ToString().ToLower()),
                
                // Metadata section (equivalent to individual streams in real format)
                new XElement("Metadata",
                    new XElement("Title", post.Title),
                    new XElement("Author", post.Author),
                    new XElement("BlogName", post.BlogName),
                    new XElement("Excerpt", new XCData(post.Excerpt))
                ),
                
                // Content section (equivalent to "Contents" stream in real format)
                new XElement("Content", new XCData(post.Content)),
                
                // Categories and Tags (equivalent to structured data in real format)
                new XElement("Categories",
                    post.Categories.Select(cat => new XElement("Category", cat))
                ),
                
                new XElement("Tags",
                    post.Tags.Select(tag => new XElement("Tag", tag))
                ),
                
                // Placeholder for supporting files storage
                new XElement("SupportingFiles",
                    new XComment("In real .wpost files, this would be a separate storage container")
                ),
                
                // Additional metadata
                new XElement("Properties",
                    new XElement("Status", post.IsDraft ? "draft" : "publish"),
                    new XElement("Format", "standard")
                )
            )
        );

        // Save the document
        document.Save(filePath);
    }

    public static BlogPost LoadPost(string filePath)
    {
        // Try to determine the file format and load accordingly
        var extension = Path.GetExtension(filePath).ToLower();
        
        if (extension == ".wpost")
        {
            return LoadPostFromStructuredStorage(filePath);
        }
        else
        {
            // Legacy XML format support
            return LoadPostFromXml(filePath);
        }
    }
    
    private static BlogPost LoadPostFromStructuredStorage(string filePath)
    {
        // This reads our XML-based approximation of the .wpost structure
        // A real implementation would use COM Structured Storage APIs to read individual streams
        
        try
        {
            var document = XDocument.Load(filePath);
            var root = document.Root!;

            var post = new BlogPost
            {
                BlogId = root.Attribute("blog-id")?.Value ?? string.Empty,
                PostId = root.Attribute("post-id")?.Value ?? Guid.NewGuid().ToString(),
                CreatedDate = DateTime.Parse(root.Attribute("created-date")?.Value ?? DateTime.Now.ToString()),
                IsDraft = bool.Parse(root.Attribute("is-draft")?.Value ?? "true")
            };

            // Read metadata section
            var metadata = root.Element("Metadata");
            if (metadata != null)
            {
                post.Title = metadata.Element("Title")?.Value ?? string.Empty;
                post.Author = metadata.Element("Author")?.Value ?? Environment.UserName;
                post.BlogName = metadata.Element("BlogName")?.Value ?? "My Blog";
                post.Excerpt = metadata.Element("Excerpt")?.Value ?? string.Empty;
            }

            // Read content
            post.Content = root.Element("Content")?.Value ?? string.Empty;

            // Read categories
            var categoriesElement = root.Element("Categories");
            if (categoriesElement != null)
            {
                post.Categories.AddRange(
                    categoriesElement.Elements("Category").Select(c => c.Value)
                );
            }

            // Read tags
            var tagsElement = root.Element("Tags");
            if (tagsElement != null)
            {
                post.Tags.AddRange(
                    tagsElement.Elements("Tag").Select(t => t.Value)
                );
            }

            return post;
        }
        catch (Exception)
        {
            // If XML parsing fails, try to read as legacy binary format
            return LoadPostFromBinaryFormat(filePath);
        }
    }
    
    private static BlogPost LoadPostFromBinaryFormat(string filePath)
    {
        // Fallback for the old binary format we created earlier
        using var fileStream = new FileStream(filePath, FileMode.Open, FileAccess.Read);
        using var reader = new BinaryReader(fileStream);
        
        try
        {
            // Read and verify header
            var signature = reader.ReadBytes(5);
            if (!signature.SequenceEqual(new byte[] { 0x57, 0x50, 0x4F, 0x53, 0x54 }))
            {
                throw new InvalidDataException("Invalid .wpost file format");
            }
            
            var version = reader.ReadByte();
            var reserved = reader.ReadInt16();
            
            var post = new BlogPost
            {
                Title = ReadString(reader),
                Content = ReadString(reader),
                Excerpt = ReadString(reader),
                Author = ReadString(reader),
                BlogName = ReadString(reader),
                BlogId = ReadString(reader),
                PostId = ReadString(reader),
                CreatedDate = DateTime.FromBinary(reader.ReadInt64()),
                IsDraft = reader.ReadBoolean()
            };
            
            // Skip modified date
            reader.ReadInt64();
            
            // Read categories
            var categoryCount = reader.ReadInt32();
            for (int i = 0; i < categoryCount; i++)
            {
                post.Categories.Add(ReadString(reader));
            }
            
            // Read tags
            var tagCount = reader.ReadInt32();
            for (int i = 0; i < tagCount; i++)
            {
                post.Tags.Add(ReadString(reader));
            }
            
            return post;
        }
        catch
        {
            throw new InvalidDataException("Unable to read .wpost file - unsupported format");
        }
    }

