    private static void SavePostAsWpost(BlogPost post, string filePath)
    {
        using var fileStream = new FileStream(filePath, FileMode.Create, FileAccess.Write);
        using var writer = new BinaryWriter(fileStream);

        // Write .wpost file header
        writer.Write(new byte[] { 0x57, 0x50, 0x4F, 0x53, 0x54 }); // "WPOST" signature
        writer.Write((byte)0x01); // Version
        writer.Write((short)0x00); // Reserved

        // Write post metadata section
        WriteString(writer, post.Title);
        WriteString(writer, post.Content);
        WriteString(writer, post.Excerpt);
        WriteString(writer, post.Author);
        WriteString(writer, post.BlogName);
        WriteString(writer, post.BlogId);
        WriteString(writer, post.PostId);

        // Write dates
        writer.Write(post.CreatedDate.ToBinary());
        writer.Write(DateTime.Now.ToBinary()); // Modified date

        // Write flags
        writer.Write(post.IsDraft);

        // Write categories count and data
        writer.Write(post.Categories.Count);
        foreach (var category in post.Categories)
        {
            WriteString(writer, category);
        }

        // Write tags count and data
        writer.Write(post.Tags.Count);
        foreach (var tag in post.Tags)
        {
            WriteString(writer, tag);
        }

        // Write end marker
        writer.Write(new byte[] { 0x45, 0x4E, 0x44 }); // "END"
    }

    private static void WriteString(BinaryWriter writer, string value)
    {
        var bytes = System.Text.Encoding.UTF8.GetBytes(value ?? string.Empty);
        writer.Write(bytes.Length);
        writer.Write(bytes);
    }"}

