            stream.Write(bytes, (uint)bytes.Length, out uint bytesWritten);
            stream.Commit(Ole32.STGC_DEFAULT);
        }
        finally
        {
            if (stream != null)
            {
                Marshal.ReleaseComObject(stream);
            }
        }
    }

