{"content": "using ChatExportParser;

class Program
{
    static void Main(string[] args)
    {
        Console.WriteLine("Chat Export Parser");
        Console.WriteLine("==================");
        Console.WriteLine();

        // Default to the fridayLinks1.json file in the parent directory
        string defaultJsonPath = @"d:\eu\GitHub\variousTests\fridayLinks1.json";
        string jsonFilePath = defaultJsonPath;

        // Check if a file path was provided as an argument
        if (args.Length > 0)
        {
            jsonFilePath = args[0];
        }
        else
        {
            Console.WriteLine($"Using default file: {defaultJsonPath}");
            Console.WriteLine("You can also specify a different file as a command line argument.");
            Console.WriteLine();
        }

        // Check if file exists
        if (!File.Exists(jsonFilePath))
        {
            Console.WriteLine($"Error: File not found - {jsonFilePath}");
            Console.WriteLine("Please provide a valid JSON file path.");
            return;
        }

        Console.WriteLine($"Parsing file: {jsonFilePath}");
        Console.WriteLine();

        // Parse the chat export
        var chatExport = ChatParser.ParseChatExport(jsonFilePath);
        if (chatExport == null)
        {
            Console.WriteLine("Failed to parse the JSON file.");
            return;
        }

        // Extract conversations
        var conversations = ChatParser.ExtractConversations(chatExport);

        Console.WriteLine($"Found {conversations.Count} conversation exchanges");
        Console.WriteLine($"Between: {chatExport.RequesterUsername} and {chatExport.ResponderUsername}");
        Console.WriteLine();

        // Display options
        while (true)
        {
            Console.WriteLine("Choose an option:");
            Console.WriteLine("1. Display all conversations");
            Console.WriteLine("2. Display conversations with index numbers");
            Console.WriteLine("3. Search for specific text in questions");
            Console.WriteLine("4. Search for specific text in responses");
            Console.WriteLine("5. Export conversations to text file");
            Console.WriteLine("6. Exit");
            Console.Write("Enter your choice (1-6): ");

            var choice = Console.ReadLine();
            Console.WriteLine();

            switch (choice)
            {
                case "1":
                    DisplayAllConversations(conversations);
                    break;
                case "2":
                    DisplayConversationsWithIndex(conversations);
                    break;
                case "3":
                    SearchInQuestions(conversations);
                    break;
                case "4":
                    SearchInResponses(conversations);
                    break;
                case "5":
                    ExportToTextFile(conversations, chatExport);
                    break;
                case "6":
                    Console.WriteLine("Goodbye!");
                    return;
                default:
                    Console.WriteLine("Invalid choice. Please try again.");
                    break;
            }

            Console.WriteLine();
            Console.WriteLine("Press any key to continue...");
            Console.ReadKey();
            Console.Clear();
        }
    }

    static void DisplayAllConversations(List<ConversationItem> conversations)
    {
        foreach (var conversation in conversations)
        {
            Console.WriteLine($"[{conversation.Timestamp:yyyy-MM-dd HH:mm:ss}]");
            Console.WriteLine($"User: {conversation.UserQuestion}");
            Console.WriteLine();
            Console.WriteLine($"AI: {conversation.AiResponse}");
            Console.WriteLine();
            Console.WriteLine(new string('-', 80));
            Console.WriteLine();
        }
    }

    static void DisplayConversationsWithIndex(List<ConversationItem> conversations)
    {
        for (int i = 0; i < conversations.Count; i++)
        {
            var conversation = conversations[i];
            Console.WriteLine($"=== Conversation #{i + 1} ===");
            Console.WriteLine($"[{conversation.Timestamp:yyyy-MM-dd HH:mm:ss}]");
            Console.WriteLine($"User: {conversation.UserQuestion}");
            Console.WriteLine();
            Console.WriteLine($"AI: {conversation.AiResponse}");
            Console.WriteLine();
            Console.WriteLine(new string('-', 80));
            Console.WriteLine();
        }
    }

    static void SearchInQuestions(List<ConversationItem> conversations)
    {
        Console.Write("Enter search term for user questions: ");
        var searchTerm = Console.ReadLine();

        if (string.IsNullOrWhiteSpace(searchTerm))
        {
            Console.WriteLine("Invalid search term.");
            return;
        }

        var matches = conversations
            .Where(c => c.UserQuestion.Contains(searchTerm, StringComparison.OrdinalIgnoreCase))
            .ToList();

        Console.WriteLine($"\nFound {matches.Count} matches for '{searchTerm}' in user questions:");
        Console.WriteLine();

        foreach (var match in matches)
        {
            Console.WriteLine($"[{match.Timestamp:yyyy-MM-dd HH:mm:ss}]");
            Console.WriteLine($"User: {match.UserQuestion}");
            Console.WriteLine($"AI: {match.AiResponse}");
            Console.WriteLine();
            Console.WriteLine(new string('-', 80));
            Console.WriteLine();
        }
    }

    static void SearchInResponses(List<ConversationItem> conversations)
    {
        Console.Write("Enter search term for AI responses: ");
        var searchTerm = Console.ReadLine();

        if (string.IsNullOrWhiteSpace(searchTerm))
        {
            Console.WriteLine("Invalid search term.");
            return;
        }

        var matches = conversations
            .Where(c => c.AiResponse.Contains(searchTerm, StringComparison.OrdinalIgnoreCase))
            .ToList();

        Console.WriteLine($"\nFound {matches.Count} matches for '{searchTerm}' in AI responses:");
        Console.WriteLine();

        foreach (var match in matches)
        {
            Console.WriteLine($"[{match.Timestamp:yyyy-MM-dd HH:mm:ss}]");
            Console.WriteLine($"User: {match.UserQuestion}");
            Console.WriteLine($"AI: {match.AiResponse}");
            Console.WriteLine();
            Console.WriteLine(new string('-', 80));
            Console.WriteLine();
        }
    }

    static void ExportToTextFile(List<ConversationItem> conversations, ChatExport chatExport)
    {
        var fileName = $"chat_export_{DateTime.Now:yyyyMMdd_HHmmss}.txt";
        var filePath = Path.Combine(Directory.GetCurrentDirectory(), fileName);

        try
        {
            using var writer = new StreamWriter(filePath);

            writer.WriteLine($"Chat Export - {chatExport.RequesterUsername} and {chatExport.ResponderUsername}");
            writer.WriteLine($"Exported on: {DateTime.Now:yyyy-MM-dd HH:mm:ss}");
            writer.WriteLine($"Total conversations: {conversations.Count}");
            writer.WriteLine();
            writer.WriteLine(new string('=', 100));
            writer.WriteLine();

            for (int i = 0; i < conversations.Count; i++)
            {
                var conversation = conversations[i];
                writer.WriteLine($"=== Conversation #{i + 1} ===");
                writer.WriteLine($"Timestamp: {conversation.Timestamp:yyyy-MM-dd HH:mm:ss}");
                writer.WriteLine($"Request ID: {conversation.RequestId}");
                writer.WriteLine();
                writer.WriteLine($"USER QUESTION:");
                writer.WriteLine(conversation.UserQuestion);
                writer.WriteLine();
                writer.WriteLine($"AI RESPONSE:");
                writer.WriteLine(conversation.AiResponse);
                writer.WriteLine();
                writer.WriteLine(new string('-', 100));
                writer.WriteLine();
            }

            Console.WriteLine($"Successfully exported to: {filePath}");
        }
        catch (Exception ex)
        {
            Console.WriteLine($"Error exporting to file: {ex.Message}");
        }
    }
