        Write(ExportDate.ToString("MMM dd, yyyy"));
        WriteLiteral("</div>\n            <div class=\"stat-label\">Export Date</div>\n        </div>\n        <div class=\"stat-item\">\n            <div class=\"stat-number\">");
        Write(Conversations.Count(c => !string.IsNullOrWhiteSpace(c.UserQuestion)));
        WriteLiteral("</div>\n            <div class=\"stat-label\">User Questions</div>\n        </div>\n    </div>\n\n    <div class=\"search-box\">\n        <input type=\"text\" class=\"search-input\" placeholder=\" Search conversations...\" id=\"searchInput\">\n    </div>\n\n    <div id=\"conversations\">");

        for (int i = 0; i < Conversations.Count; i++)
        {
            var conversation = Conversations[i];
            WriteLiteral("\n        <div class=\"conversation\" data-conversation=\"");
            Write(i + 1);
            WriteLiteral("\">\n            <div class=\"conversation-header\">\n                <span class=\"conversation-number\">#");
            Write(i + 1);
            WriteLiteral("</span>\n                <span class=\"conversation-date\">");
            Write(conversation.Timestamp.ToString("yyyy-MM-dd HH:mm:ss"));
            WriteLiteral("</span>\n            </div>");

            if (!string.IsNullOrWhiteSpace(conversation.UserQuestion))
