                var userInitial = RequesterUsername.Length > 0 ? RequesterUsername.Substring(0, 1).ToUpper() : "U";
                WriteLiteral("\n            <div class=\"message\">\n                <div class=\"message-header\">\n                    <div class=\"avatar user-avatar\">");
