    <meta name=""viewport"" content=""width=device-width, initial-scale=1.0"">
    <title>Chat Export - {data.RequesterUsername} and {data.ResponderUsername}</title>
    {GetStyles()}
</head>
<body>
    {GetHeader(data)}
    {GetStats(data)}
    {GetSearchBox()}
    {GetConversations(data)}
    {GetFooter(data)}
    {GetJavaScript()}
</body>
</html>";
    }

